#include <iostream>#include <string>#include <set>#include <fstream>#include <ostream>#include <locale>#include <vector>#include <stack>#include <map>#include <list>using namespace std;void writeSet(string fileName){        set<string> mySet;    set<string>::iterator iter;    string word = "";    char letter;    ifstream fin;    ofstream fout;        //Create set    fin.open(fileName + ".txt");    while (!fin.fail()){        letter = fin.get();                if (isalpha(letter)){            word += letter;        } else{            if(isalpha(word.front())){                mySet.insert(word);                word = "";            } else{                word = "";            }        }    }    fin.close();        //Print set to file    fout.open(fileName + "_set.txt");    for (iter = mySet.begin(); iter != mySet.end(); iter++) {        fout << *iter << endl;    }    fout.close();}void writeVector(string fileName){        vector<string> myVector;    vector<string>::iterator iterV;    ifstream fin;    ofstream fout;    string word = "";    char letter;        //Create vector    fin.open(fileName + ".txt");    while (!fin.fail()){        letter = fin.get();        if (isalpha(letter)){            word += letter;        } else{            if(isalpha(word.front())){                myVector.push_back(word);                word = "";            } else{                word = "";            }        }    }    fin.close();        //Print vector to file    fout.open(fileName + "_vector.txt");    for (iterV = myVector.begin(); iterV != myVector.end(); iterV++){        fout << *iterV << endl;    }    fout.close();    }void writeMap(string fileName){        vector<string> myVector;    vector<string>::iterator iterV;    ifstream fin;    ofstream fout;    string word = "";    char letter;        //Recreate vector    fin.open(fileName + ".txt");    while (!fin.fail()){        letter = fin.get();        if (isalpha(letter)){            word += letter;        } else{            if(isalpha(word.front())){                myVector.push_back(word);                word = "";            } else{                word = "";            }        }    }    fin.close();        //Convert vector to map    map<string, string> wordmap;    map<string, string>::iterator iterM;    string last = "";        for (iterV = myVector.begin(); iterV != myVector.end(); iterV++) {        wordmap[last] = *iterV;        last = *iterV;    }        //Generate new text with map    string state = "";        for(int i = 0; i < 100; i++){        cout << wordmap[state] << " ";        state = wordmap[state];    }    cout << endl;        //Print map to file    fout.open(fileName + "_1_1.txt");    for (iterM = wordmap.begin(); iterM != wordmap.end(); iterM++){        fout << iterM->first << ", " << iterM->second << endl;    }    fout.close();    }void writeBetterMap(string fileName){        vector<string> myVector;    vector<string>::iterator iterV;    ifstream fin;    ofstream fout;    string word = "";    char letter;        //Recreate vector    fin.open(fileName + ".txt");    while (!fin.fail()){        letter = fin.get();        if (isalpha(letter)){            word += letter;        } else{            if(isalpha(word.front())){                myVector.push_back(word);                word = "";            } else{                word = "";            }        }    }    fin.close();        //Create better map    map<string, vector<string>> wordmap;    map<string, vector<string>>::iterator iterM;    string state = "";        for(iterV = myVector.begin(); iterV != myVector.end(); iterV++) {        if(*iterV != ""){            wordmap[state].push_back(*iterV);        }        state = *iterV;    }        //Output Nephi key and values    for (iterM = wordmap.begin(); iterM != wordmap.end(); iterM++){        if (iterM->first == "Nephi"){            cout << "Key: " << (*iterM).first << endl << "Values: ";            for (int i = 0; i < (*iterM).second.size(); i++){                cout << (*iterM).second.at(i) << ", ";            }            cout << endl << endl;        }    }        //Generate better text    srand(time(NULL));    state = "";    for (int i = 0; i < 100; i++) {        int ind = rand() % wordmap[state].size();        cout << wordmap[state][ind] << " ";        state = wordmap[state][ind];    }    cout << endl;    }void writeBestMap(string fileName){        vector<string> myVector;    vector<string>::iterator iterV;    ifstream fin;    ofstream fout;    string word = "";    char letter;        //Recreate vector    fin.open(fileName + ".txt");    while (!fin.fail()){        letter = fin.get();        if (isalpha(letter)){            word += letter;        } else{            if(isalpha(word.front())){                myVector.push_back(word);                word = "";            } else{                word = "";            }        }    }    fin.close();        //Create best map    int M = 2;    map<list<string>, vector<string>> wordmap;    list<string> state;        for(int i = 0; i < M; i++) {        state.push_back("");    }    for (iterV = myVector.begin(); iterV != myVector.end(); iterV++) {        wordmap[state].push_back(*iterV);        state.push_back(*iterV);        state.pop_front();    }        //Generate best text    state.clear();    for (int i = 0; i < M; i++) {        state.push_back("");    }    for (int i = 0; i < 100; i++) {        int ind = rand() % wordmap[state].size();        cout << wordmap[state][ind]<<" ";        state.push_back(wordmap[state][ind]);        state.pop_front();    }        //Print best text to file    fout.open(fileName + "_NewText.txt");    state.clear();        for (int i = 0; i < M; i++) {        state.push_back("");    }    for (int i = 0; i < 100; i++) {        int ind = 0;        if(wordmap[state].size() != 0){            ind = rand() % wordmap[state].size();        }        fout << wordmap[state][ind]<<" ";        state.push_back(wordmap[state][ind]);        state.pop_front();    }    fout.close();    }int main(int argc, char* argv[]){        for (int i = 1; i < argc; i++){                string fileName = "";        fileName += argv[i];                writeSet(fileName);                writeVector(fileName);                writeMap(fileName);                writeBetterMap(fileName);                writeBestMap(fileName);            }        return 0;}